mapper.select:

select * from 
(
	select rownum as rn, j_no, j_title, j_u_id, j_u_name, j_start_date, j_end_date, j_start_time, j_end_time, j_area, j_date, j_intro, j_pic, j_view
	from (
		select * from job
		where
		j_title like  '%'||#{search}||'%'
		or j_intro like  '%'||#{search}||'%'
		order by j_no desc
		)
)
where rn >= #{start} and rn <= #{end}
