mapper.select:
select *
from (
	select rownum as rn, rt_no, rt_u_id, rt_title, rt_rest_name, rt_rest_img, rt_rest_type, rt_rest_addr1, rt_rest_addr2, rt_con_type, rt_con_age, rt_con_gender, rt_start_date, rt_end_date, rt_start_time, rt_end_time, rt_date, rt_content, rt_pay, rt_view,
			u_id, u_pw, u_name, u_address, u_address2, u_birth, u_type, u_intro, u_gender, u_profile, u_phonNumber 
	from (
		select * from user_info, recruit
		where rt_u_id = u_id
		order by rt_no desc
	)
) 
where rn >= #{start} and rn <= #{end}

select * 
from (
	select * from user_info, recruit
	where rt_u_id = u_id
	order by rt_no desc
) 
where rt_no = #{rt_no}

choose.when:
select *
from (
	select rownum as rn, rt_no, rt_u_id, rt_title, rt_rest_name, rt_rest_img, rt_rest_type, rt_rest_addr1, rt_rest_addr2, rt_con_type, rt_con_age, rt_con_gender, rt_start_date, rt_end_date, rt_start_time, rt_end_time, rt_date, rt_content, rt_pay, rt_view,
			u_id, u_pw, u_name, u_address, u_address2, u_birth, u_type, u_intro, u_gender, u_profile, u_phonNumber 
	from (
		select * from user_info, recruit
		where rt_u_id = u_id
		order by rt_no desc
	)
	where rt_rest_addr2 like '%'||#{recruitResearchMainInput}||'%' and rt_start_date >= #{recruitResearchStartDate} and rt_end_date <= #{recruitResearchEndDate} and rt_start_time >= #{recruitResearchStartTime} and rt_end_time <= #{recruitResearchEndTime} order by rt_no desc
) 
where rn >= #{startPage} and rn <= #{endPage}

choose.otherwise:
select *
from (
	select rownum as rn, rt_no, rt_u_id, rt_title, rt_rest_name, rt_rest_img, rt_rest_type, rt_rest_addr1, rt_rest_addr2, rt_con_type, rt_con_age, rt_con_gender, rt_start_date, rt_end_date, rt_start_time, rt_end_time, rt_date, rt_content, rt_pay, rt_view,
			u_id, u_pw, u_name, u_address, u_address2, u_birth, u_type, u_intro, u_gender, u_profile, u_phonNumber 
	from (
		select * from user_info, recruit
		where rt_u_id = u_id
		order by rt_no desc
	)
	where rt_title like '%'||#{recruitResearchMainInput}||'%' and rt_start_date >= #{recruitResearchStartDate} and rt_end_date <= #{recruitResearchEndDate} and rt_start_time >= #{recruitResearchStartTime} and rt_end_time <= #{recruitResearchEndTime} order by rt_no desc
) 
where rn >= #{startPage} and rn <= #{endPage}
