mapper.select:
select *
from (
	select rownum as rn, rt_no, rt_u_id, rt_title, rt_rest_name, rt_start_date, rt_end_date, rt_start_time, rt_end_time, rt_date, rt_content, rt_pay, rt_view,
			u_id, u_pw, u_name, u_address, u_address2, u_birth, u_type, u_intro, u_gender, u_profile, u_phonNumber 
	from (
		select * from user_info, recruit
		where rt_u_id = u_id
		order by rt_no desc
	)
) 
where rn >= #{start} and rn <= #{end}
